package it.gov.pagopa.atmlayer.service.consolebackend.resource;

import io.quarkus.test.junit.QuarkusTest;
import io.restassured.http.Header;
import io.smallrye.mutiny.Uni;
import jakarta.ws.rs.container.ContainerRequestContext;
import jakarta.ws.rs.core.MediaType;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import java.util.HashMap;

import static io.restassured.RestAssured.given;
import static it.gov.pagopa.atmlayer.service.consolebackend.utils.HeadersUtils.getEmailJWT;
import static org.junit.Assert.assertEquals;
import static org.mockito.Mockito.*;

@QuarkusTest
class InfoResourceTest {

    Header authHeader;

    @BeforeEach
    void initAuthHeader(){
        authHeader = new Header("Authorization", "eyJraWQiOiJ5Uk92cjRsa2dmb3U1UURCZGM1RzI5cEtSVWxyRHVONW9oSTN2a0padXFVPSIsImFsZyI6IlJTMjU2In0..OX02xa6siwktsbKbd9PH_C2xFYjpnMckipx6xLcCzx2iAxhV7ghMeDNqe1sk1mcXDxRatUexTS4nvynSlTy6wWLe81GSZTtkAvlUWQ_-mJT90BDxJp8rNC7IPqUS_4Q7QkMOuNHzh_0nYklIa_w8-sV93I3dJ61sAQUw1ye0kxu3lZ7NKUiBU07W2RH2YcdgWP4yr50s9CQMySYymPs_CP1w8eDm0vNndswP9uN4x3YRe-idQ9Q7qUPL4iZzqftahvODzU7mhTnm_IbMr9mZfEuyLNP070PeyGI7giIWdJStTZ0-8tblVAZ_DuoDkkVOHMPAL55yJTx3HRJ5beK4Uw&token_type=Bearer&expires_in=86400");
    }

    @Test
    void testGetTokenEmail() {
        String email = "antonio.tarricone@pagopa.it";
        ContainerRequestContext container = mock(ContainerRequestContext.class);
        doReturn(authHeader.toString()).when(container).getHeaderString("Authorization");
        when(getEmailJWT(container)).thenReturn(Uni.createFrom().item(email).toString());
        HashMap<String, String> response = new HashMap<>();
        response.put("email", email);
        HashMap result = given()
                .header(authHeader)
                .contentType(MediaType.APPLICATION_JSON)
                .when().get("/api/v1/console-service/info/email")
                .then()
                .statusCode(200)
                .extract()
                .body()
                .as(HashMap.class);
        assertEquals(response, result);

    }

    @Test
    void testGetTokenEmail_Exception() {
        String email = "utente@pagopa.it";
        HashMap<String, String> response = new HashMap<>();
        response.put("email", email);
        HashMap result = given()
                .contentType(MediaType.APPLICATION_JSON)
                .when().get("/api/v1/console-service/info/email")
                .then()
                .statusCode(200)
                .extract()
                .body()
                .as(HashMap.class);
        assertEquals(response, result);

    }
}